frappe.ready(function () {
    console.log("Client Intake – About Me: web form script loaded");

    // Run once the web form has finished loading
    frappe.web_form.on("after_load", () => {
        setTimeout(function () {
            setup_household_mobile_cards();
            auto_add_primary_client_row();
        }, 300);
    });

    // ------------------------------------------------------------
    // 1) Auto-populate Household Member Detail
    // ------------------------------------------------------------
    function auto_add_primary_client_row() {
        // Get the child table field
        const field = frappe.web_form.get_field("household_member_detail");
        if (!field || !field.grid) {
            console.log("Household member grid not found on web form.");
            return;
        }

        // If there is already at least one row (e.g., user is editing), do nothing
        const existing_rows =
            (frappe.web_form.doc && frappe.web_form.doc.household_member_detail) || [];
        if (existing_rows && existing_rows.length > 0) {
            return;
        }

        const grid = field.grid;

        // Read values from the parent form
        const client_name = frappe.web_form.get_value("client_name") || "";
        const client_dob  = frappe.web_form.get_value("date_of_birth") || null;

        // Add a new row to the child table
        const row = grid.add_new_row();

        // Map parent values into child fields
        row.member_name = client_name;            // Household Member.member_name
        row.relationship = "Self";                // Household Member.relationship
        row.is_this_the_primary_client = 1;       // mark as primary client

        // Only set DOB if a value is present
        if (client_dob) {
            row.member_dob = client_dob;         // Household Member.member_dob
        }

        // Optionally set total household size to 1 on first load
        if (!frappe.web_form.get_value("total_household_size")) {
            frappe.web_form.set_value("total_household_size", 1);
        }

        grid.refresh();
    }

    // ------------------------------------------------------------
    // 2) Mobile-friendly cards for Household Member Detail
    // ------------------------------------------------------------
    function setup_household_mobile_cards() {
        const field = frappe.web_form.get_field("household_member_detail");
        if (!field || !field.grid) {
            console.log("Household member grid not found on web form (for styling).");
            return;
        }

        $(field.grid.wrapper).addClass("mobile-card-view");

        if (!$("#mobile-card-css").length) {
            $("head").append(`
                <style id="mobile-card-css">
                    .mobile-card-view .grid-heading-row {
                        display: none !important;
                    }
                    .mobile-card-view .grid-body .grid-row {
                        display: block !important;
                        border: 1px solid #d1d8dd;
                        border-left: 4px solid #5e64ff;
                        border-radius: 8px;
                        margin-bottom: 15px;
                        padding: 15px;
                        background-color: #ffffff;
                        box-shadow: 0 2px 5px rgba(0,0,0,0.05);
                    }
                    .mobile-card-view .grid-body .grid-row .col {
                        width: 100% !important;
                        padding: 5px 0 !important;
                        border-bottom: 1px solid #f0f0f0;
                        display: flex;
                        justify-content: space-between;
                    }
                    .mobile-card-view .grid-body .grid-row .col:last-child {
                        border-bottom: none;
                    }
                </style>
            `);
        }
    }
});

frappe.ready(function () {
    console.log("Client Intake – Housing & Utilities web form script loaded");

    frappe.web_form.on("after_load", () => {
        init_from_hrsn_case();
        setup_dynamic_helpers();
    });

    // ------------------------------------------------------------
    // 1) Prefill from HRSN Case (if provided)
    // ------------------------------------------------------------
    function init_from_hrsn_case() {
        let hrsn_case = frappe.web_form.get_value("hrsn_case");

        // If not already set, try to read from URL query param ?hrsn_case=CASE-0001
        if (!hrsn_case) {
            try {
                const params = new URLSearchParams(window.location.search || "");
                const paramVal = params.get("hrsn_case");
                if (paramVal) {
                    hrsn_case = paramVal;
                    frappe.web_form.set_value("hrsn_case", hrsn_case);
                }
            } catch (e) {
                console.log("Could not parse URL params for hrsn_case:", e);
            }
        }

        if (!hrsn_case) {
            return;
        }

        // Fetch HRSN Case to prefill client_name (and anything else you want)
        frappe.call({
            method: "frappe.client.get",
            args: {
                doctype: "HRSN Case",
                name: hrsn_case
            },
            callback: function (r) {
                if (!r || !r.message) return;
                const c = r.message;

                // Only set values if empty on the webform
                if (c.client_name && !frappe.web_form.get_value("client_name")) {
                    frappe.web_form.set_value("client_name", c.client_name);
                }

                // OPTIONAL: If these fields exist on HRSN Case, you can uncomment
                // and map them too.
                //
                // if (c.current_housing_status &&
                //     !frappe.web_form.get_value("current_housing_status")) {
                //     frappe.web_form.set_value("current_housing_status", c.current_housing_status);
                // }
                //
                // if (c.monthly_rent_amount &&
                //     !frappe.web_form.get_value("monthly_rent_amount")) {
                //     frappe.web_form.set_value("monthly_rent_amount", c.monthly_rent_amount);
                // }
            }
        });
    }

    // ------------------------------------------------------------
    // 2) Gentle helpers / prompts
    // ------------------------------------------------------------
    function setup_dynamic_helpers() {
        // If rent is past due, prompt for months
        frappe.web_form.on("past_due_rent", (field, value) => {
            if (value === "Yes") {
                frappe.msgprint({
                    title: __("Rent past due"),
                    message: __("Please tell us how many months past due your rent is in the 'How many months past due?' field."),
                    indicator: "orange"
                });
            }
        });

        // If utilities are in arrears, prompt for months in arrears
        frappe.web_form.on("in_arrears", (field, value) => {
            if (value === "Yes") {
                frappe.msgprint({
                    title: __("Utilities in arrears"),
                    message: __("Please let us know how many months in arrears you are in the 'How many months in arrears are you?' field."),
                    indicator: "orange"
                });
            }
        });

        // When client indicates they have an active lease, suggest uploading it
        frappe.web_form.on("has_active_lease", (field, value) => {
            if (value === "Yes") {
                frappe.msgprint({
                    title: __("Lease upload recommended"),
                    message: __("If you have a copy of your lease, please upload it. This helps us process your housing request faster."),
                    indicator: "blue"
                });
            }
        });
    }
});

frappe.ready(function () {
    console.log("ROI Attestations & Certifications web form script loaded");

    frappe.web_form.on("after_load", () => {
        init_from_hrsn_case();
        ensure_default_cco_placeholder();
    });

    // ------------------------------------------------------------
    // 1) Prefill from HRSN Case (if provided)
    // ------------------------------------------------------------
    function init_from_hrsn_case() {
        let hrsn_case = frappe.web_form.get_value("hrsn_case");

        // If not already set, try to read from URL query param ?hrsn_case=CASE-0001
        if (!hrsn_case) {
            try {
                const params = new URLSearchParams(window.location.search || "");
                const paramVal = params.get("hrsn_case");
                if (paramVal) {
                    hrsn_case = paramVal;
                    frappe.web_form.set_value("hrsn_case", hrsn_case);
                }
            } catch (e) {
                console.log("Could not parse URL params for hrsn_case:", e);
            }
        }

        if (!hrsn_case) {
            // No case → still set a generic placeholder in the ROI text
            ensure_default_cco_placeholder();
            return;
        }

        // Fetch HRSN Case to prefill client_name and CCO display
        frappe.call({
            method: "frappe.client.get",
            args: {
                doctype: "HRSN Case",
                name: hrsn_case
            },
            callback: function (r) {
                if (!r || !r.message) {
                    ensure_default_cco_placeholder();
                    return;
                }

                const c = r.message;

                // Prefill client_name if empty
                if (c.client_name && !frappe.web_form.get_value("client_name")) {
                    frappe.web_form.set_value("client_name", c.client_name);
                }

                // Try to determine CCO name from HRSN Case
                // Adjust these fieldnames as needed to match your HRSN Case doctype
                const cco_name =
                    c.cco_name ||
                    c.cco ||
                    c.health_plan ||
                    c.plan_name ||
                    "";

                update_cco_display(cco_name);
            }
        });
    }

    // ------------------------------------------------------------
    // 2) Update the CCO placeholder span in the HTML
    // ------------------------------------------------------------
    function update_cco_display(cco_name) {
        const span = document.getElementById("cco_display");
        if (!span) {
            console.log("cco_display span not found in ROI HTML");
            return;
        }

        if (cco_name) {
            span.textContent = cco_name;
        } else {
            // Fallback generic text if we cannot resolve a specific CCO
            span.textContent = "your Coordinated Care Organization (CCO)";
        }
    }

    function ensure_default_cco_placeholder() {
        // If nothing has been inserted yet, set a generic phrase
        const span = document.getElementById("cco_display");
        if (!span) return;

        const current = (span.textContent || "").trim();
        if (!current) {
            span.textContent = "your Coordinated Care Organization (CCO)";
        }
    }
});

frappe.ready(function () {
  // 1) Pre-fill document_type from querystring (Ledger, W9, etc.)
  const params = new URLSearchParams(window.location.search);
  const dt = params.get("document_type");
  if (dt) frappe.web_form.set_value("document_type", dt);

  // 2) Lock vendor field (we'll fill it server-side with a whitelisted method)
  frappe.web_form.set_df_property("vendor", "read_only", 1);

  // 3) Fetch vendor linked to this portal user (requires a small server method)
  frappe.call({
    method: "portalis_app.api.vendor_portal.get_vendor_for_current_user",
    callback: (r) => {
      if (r.message) frappe.web_form.set_value("vendor", r.message);
    }
  });
});
